(23, 'You\'ve registered with DRH Sports', 'User Registration', '<p>Hi&nbsp;{user_name}</p>\r\n\r\n<p>You have successfully registered with us at&nbsp;<strong>DRH Sports, </strong>the best in sports coaching!</p>\r\n\r\n<p>We&#39;re so happy that you&#39;ve decided to join us. Wheether you&#39;re looking for a tennis coaching, a coaching class, a holiday kids camp or an after school club, we&#39;re sure you&#39;ve made the right choice.&nbsp;</p>\r\n\r\n<p>If you have any questions about your account or the coaching course or kids camp you&#39;re looking to register for, please do not hesitate to <a href="http://demo.drhsports.co.uk/contact">contact us</a>.</p>\r\n\r\n<p>Thank you,</p>\r\n\r\n<p>The DRH Sports team</p>', '2020-09-30 04:27:35', '2020-11-05 13:42:18'),
(24, 'Coach submit a report', 'Coach Submit Report', '<p>Hello {user_name}</p>\r\n\r\n<p>Greetings from DRH Sports!<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Coach</strong> - {coach} has submit a report for their linked tennis <strong>Player </strong>- {player}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Please Check log into your DRH Sports online account to view the report.&nbsp;</p>\r\n\r\n<p>Thanks!</p>', '2020-09-30 05:50:41', '2020-10-01 10:21:08'),
(25, 'Coach Link Request Status', 'Coach accept/denied link request', '<p>Hello {user_name}</p>\r\n\r\n<p>Here is the status of &quot;<strong>Coach Link Request Status&quot;</strong>:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Coach</strong> - {coach}</p>\r\n\r\n<p><strong>Status</strong> -&nbsp;{status}&nbsp;</p>\r\n\r\n<p><strong>Player</strong> - {player}</p>\r\n\r\n<p><strong>Parent</strong> - {parent}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>111111111Thanks!</p>', '2020-09-30 07:02:03', '2020-09-30 07:21:41'),
(26, 'New Coach Request', 'New Coach Request', '<p>Hello Admin</p>\r\n\r\n<p>Greetings!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>{user_name} with {user_email} is registered as coach. You need to accept the registration in order for the coach to operate on the site.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Please check.</p>\r\n\r\n<p>Thanks!</p>\r\n\r\n<p>&nbsp;</p>', '2020-09-30 07:37:25', '2020-09-30 07:55:03'),
(27, 'New Order Placed', 'Admin Order Placed', '<p>Hello {name}</p>\r\n\r\n<p>Welcome to DRH Sports!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hope you are doing well!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Your order have been placed successfully. Thanks for connecting with us.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>{OrderDetail}</p>', '2020-09-30 08:30:08', '2020-09-30 08:30:36'),
