(17, NULL, 'Header', '<table border="0" cellpadding="0" cellspacing="0" style="background-color: #001642;" width="100%">\n	<tbody>\n		<tr>\n			<td>\n			<table 650="" align="center" border="0" cellpadding="0" cellspacing="0" style="border-collapse: collapse; \n width=">\n				<tbody>\n					<tr>\n						<td align="center" style="padding-top: 10px; padding-bottom: 10px;"><a href="#" style="display:inline-block" target="_blank"><img alt="Logo" border="0" src="http://49.249.236.30:8654/dominic-new/public/images/logo.png" style="width: 120px; height: 80px;" /></a></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>', '2020-09-21 00:50:31', '2020-09-28 03:26:11'),
(18, NULL, 'Footer', '<p style="text-align: center;"><strong>Head Office</strong> -<br />\nDRH Sports Ltd, Letchworth Sports &amp; Tennis Club, Muddy Lane, SG6 3TB<br />\n<strong>Phone&nbsp;</strong>-&nbsp;07901 903520<br />\n<strong>Email&nbsp;</strong>-&nbsp;info@drhsports.co.uk</p>\n\n<table align="center" border="0" cellpadding="0" cellspacing="0" style="border-collapse: collapse;" width="100%">\n	<tbody>\n		<tr>\n			<td style="font-family: &quot;Maven Pro&quot;, sans-serif; font-size: 16px; background: #001642; line-height: 22px; color: #fff; padding-top: 20px; padding-bottom: 20px; text-align: center;">Copyright &copy; 2020 - All Rights Reserved.</td>\n		</tr>\n	</tbody>\n</table>', '2020-09-21 00:53:00', '2020-11-26 04:16:57'),
(22, 'Information about your DRH order', 'Order Placed Information', '<p>Hi&nbsp;{name}</p>\r\n\r\n<p>Thank you for your purchase with DRH Sports. Please see some important information about your booking below.&nbsp;</p>\r\n\r\n<p>{OrderInfo}</p>\r\n\r\n<p>&nbsp;</p>', '2020-09-29 07:49:25', '2021-02-17 06:02:15'),
(23, 'You\'ve registered with DRH Sports', 'User Registration', '<p>Hi&nbsp;{user_name}</p>\r\n\r\n<p>You have successfully registered with us at&nbsp;<strong>DRH Sports, </strong>the best in sports coaching!</p>\r\n\r\n<p>We&#39;re so happy that you&#39;ve decided to join us. Whether you&#39;re looking for a tennis coaching, a coaching class, a holiday kids camp or an after school club, we&#39;re sure you&#39;ve made the right choice.&nbsp;</p>\r\n\r\n<p>If you have any questions about your account or the coaching course or kids camp you&#39;re looking to register for, please do not hesitate to <a href="http://49.249.236.30:8654/dominic-new/contact">contact us</a>.</p>\r\n\r\n<p>Thank you,</p>\r\n\r\n<p>The DRH Sports team</p>', '2020-09-30 04:27:35', '2020-12-29 14:51:14'),
(24, 'Coach submit a report', 'Coach Submit Report', '<p>Hello {user_name}</p>\r\n\r\n<p>Greetings from DRH Sports!<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Coach</strong> - {coach} has submit a report for their linked tennis <strong>Player </strong>- {player}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Please Check log into your DRH Sports online account to view the report.&nbsp;</p>\r\n\r\n<p>Thanks!</p>', '2020-09-30 05:50:41', '2020-10-01 10:21:08'),
(25, 'Coach Link Request Status', 'Coach accept/denied link request', '<p>Hello {user_name}</p>\r\n\r\n<p>Here is the status of &quot;<strong>Coach Link Request Status&quot;</strong>:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Coach</strong> - {coach}</p>\r\n\r\n<p><strong>Status</strong> -&nbsp;{status}&nbsp;</p>\r\n\r\n<p><strong>Reason Of Rejection&nbsp;</strong>-&nbsp;{reason}&nbsp;</p>\r\n\r\n<p><strong>Player</strong> - {player}</p>\r\n\r\n<p><strong>Parent</strong> - {parent}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thanks!</p>', '2020-09-30 07:02:03', '2021-02-09 00:38:53'),
(26, 'New Coach Request', 'New Coach Request', '<p>Hello Admin</p>\r\n\r\n<p>Greetings!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>{user_name} with {user_email} is registered as coach. You need to accept the registration in order for the coach to operate on the site.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Please check.</p>\r\n\r\n<p>Thanks!</p>\r\n\r\n<p>&nbsp;</p>', '2020-09-30 07:37:25', '2020-09-30 07:55:03'),
(27, 'New Order Placed', 'Admin Order Placed', '<p>Hello {name}</p>\r\n\r\n<p>Welcome to DRH Sports!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hope you are doing well!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Your order have been placed successfully. Thanks for connecting with us.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>{OrderDetail}</p>', '2020-09-30 08:30:08', '2020-09-30 08:30:36'),
(28, 'Verify Coach Account', 'Verify Coach Account', '<p>Hello {user_name}</p>\r\n\r\n<p>Greetings!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Your account status is - {status}</p>\r\n\r\n<p>Check with admin1111111`1898890989008989.</p>\r\n\r\n<p>Thanks!</p>', '2020-09-30 08:57:13', '2020-10-01 00:57:33'),
(29, 'Linked Player Upload Goal', 'Linked Player Upload Goal', '<p>Hello {user_name}</p>\r\n\r\n<p>Greetings!11</p>\r\n\r\n<p>11111444444444422222222222222222222222</p>\r\n\r\n<p>{parent}&nbsp;has uploaded a goal for his player - {child}.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Please check.&nbsp;</p>\r\n\r\n<p>Thanks!</p>', '2020-09-30 23:22:59', '2020-10-01 00:16:14'),
(30, 'Invoice Status', 'Invoice Approved/Not Approved', '<p>Hello {user_name}</p>\r\n\r\n<p>Greetings!</p>\r\n\r\n<p>Your invoice - <strong>&quot;{invoice_name}&quot;</strong> is <strong>{status}</strong>&nbsp;by DRH Sports.</p>\r\n\r\n<ul>\r\n	<li>If your invoice has not been accepted, you will need to log into your DRH Sports online account to see the reason stated.&nbsp;</li>\r\n	<li>If your invoice was accepted, you will be paid directly to your bank account within 14 days.&nbsp;</li>\r\n</ul>\r\n\r\n<p>If you have any questions about payments, please email admin@drhsports.co.uk and a member of the team will respond asap.&nbsp;</p>\r\n\r\n<p>Thanks you!</p>', '2020-10-01 02:37:22', '2020-10-01 10:35:16'),
(31, 'New Invoice Created By Coach', 'New Invoice By Coach', '<p>Hello DRH Sports</p>\r\n\r\n<p>Hope you are doing great!</p>\r\n\r\n<p><strong>{user_name}</strong>&nbsp;|<strong>&nbsp;{user_email}</strong>&nbsp; has aubmitted a new invoice -<strong> {invoice_name}</strong></p>\r\n\r\n<p>Please log into the website admin to view and process the new invoice submitted by&nbsp;<strong>{user_name}</strong></p>\r\n\r\n<p>Thanks!</p>', '2020-10-01 03:17:39', '2020-10-01 10:38:37'),
(32, 'Book A Taster Class', 'Book A Taster Class - Admin', '<p>Hello DRH Sports,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>New book a taster&nbsp;request -&gt;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Here are the details of the player who wishes to book a taster class:</p>\r\n\r\n<p>Participant name - {user_name}</p>\r\n\r\n<p>Participant DOB - {dob}</p>\r\n\r\n<p>Participant Gender - {gender}&nbsp;</p>\r\n\r\n<p>Parent Name - {parent_name}&nbsp;</p>\r\n\r\n<p>Parent Email - {parent_email}&nbsp;</p>\r\n\r\n<p>Parent Telephone - {parent_telephone}&nbsp;</p>\r\n\r\n<p>Class - {class}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>The taster session is not yet comfirmed until DRH Sports has been in contact to confirm</p>\r\n\r\n<p>Thanks!</p>', '2020-10-01 03:52:49', '2020-10-15 08:42:06'),
(33, 'Coach Qualification Expired', 'Coach Qualification Expired', '<p>Hello {user_name}</p>\r\n\r\n<p>Your qualification - {qualification} is expired.</p>\r\n\r\n<p>Thanks!</p>', '2020-10-15 05:13:11', '2020-10-15 05:13:58'),
(34, 'You\'ve booked a free taster class', 'Book A Taster Class - Parent', '<p>Hello {user_name},</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thank you for filling out the &quot;Book a taster class&quot; form at DRH Sports. Please see a confirmation of what you have requested below.</p>\r\n\r\n<p><span style="font-size:16px;"><strong>Participant name</strong></span> - {user_name}</p>\r\n\r\n<p><span style="font-size:16px;"><strong>Participant DOB</strong></span> - {dob}</p>\r\n\r\n<p><span style="font-size:16px;"><strong>Participant Gender</strong></span> - {gender}&nbsp;</p>\r\n\r\n<p><span style="font-size:16px;"><strong>Parent Name</strong></span> - {parent_name}&nbsp;</p>\r\n\r\n<p><strong><span style="font-size:16px;">Parent Email </span></strong>- {parent_email}&nbsp;</p>\r\n\r\n<p><span style="font-size:16px;"><strong>Parent Telephone</strong></span> - {parent_telephone}&nbsp;</p>\r\n\r\n<p><span style="font-size:16px;"><strong>Class</strong></span> - {class}</p>\r\n\r\n<p>A DRH Sports representative will be in touch to confirm the date/day/time of your taster class.&nbsp;</p>', '2020-10-15 08:35:09', '2020-10-15 17:16:50'),
(35, 'Coach submit "End of term Report"', 'Coach submit "End of term Report"', '<p>Hello {user_name}</p>\r\n\r\n<p>Greetings from DRH Sports!</p>\r\n\r\n<p><br />\r\nCoach - {coach} has submit the following report: {End of Term Report} for tennis Player - {player}</p>\r\n\r\n<p>Please Check log into your DRH Sports online account to view the report.</p>\r\n\r\n<p>Thanks!</p>', '2020-10-19 00:29:15', '2021-01-27 05:39:29'),
(36, 'Coach submit "Player Report"', 'Coach submit "Player Report"', '<p>Hello {user_name}</p>\r\n\r\n<p>Hope you are doing great!</p>\r\n\r\n<p>Greetings from DRH Sports!</p>\r\n\r\n<p><br />\r\nCoach - {coach} has submit the following report: {Player Report} for tennis Player - {player}</p>\r\n\r\n<p>Please Check log into your DRH Sports online account to view the report.&nbsp;</p>\r\n\r\n<p>Thanks!</p>', '2020-10-19 00:29:35', '2020-10-19 01:52:42'),
(37, 'Coach submit "Match Report"', 'Coach submit "Match Report"', '<p>Hello {user_name}</p>\r\n\r\n<p>Greetings from DRH Sports!</p>\r\n\r\n<p><br />\r\nCoach - {coach} has submit the following report: {Match Report} for their linked tennis Player - {player}</p>\r\n\r\n<p>Please Check log into your DRH Sports online account to view the report.&nbsp;</p>\r\n\r\n<p>Thanks!</p>', '2020-10-19 00:29:48', '2020-10-26 00:44:30'),
(38, 'Subscribers Email', 'Subscribers Email', '<p>Hello</p>\r\n\r\n<p>Greetings!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>You are successfully subscribed&nbsp;to our website.</p>\r\n\r\n<p>We will provide you the latest updates of the camps &amp; courses.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thanks!</p>', '2020-10-21 07:02:17', '2020-10-22 01:26:53'),
(40, 'Contact Us - User', 'Contact Us - User', '<p>Hello {user_name},</p>\n\n<p>&nbsp;</p>\n\n<p>Thank you for filling out the &quot;Contact Us&quot; form at DRH Sports. Please see a confirmation of what you have requested below.</p>\n\n<p><strong>Participant name</strong> - {user_name}</p>\n\n<p><strong>Parent Email </strong>- {parent_email}&nbsp;</p>\n\n<p><strong>Parent Telephone</strong> - {parent_telephone}&nbsp;</p>\n\n<p><strong>Subject</strong> - {subject}</p>\n\n<p><strong>Message</strong> - {message}</p>\n\n<p>A DRH Sports representative will be in touch to confirm the date/day/time of your taster class.&nbsp;</p>', '2020-11-11 02:13:15', '2020-11-11 04:00:15'),
(41, NULL, 'Contact Us - Admin', '<p>Hello DRH Sports,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>New Contact Us request -&gt;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Here are the details of the player who wishes to book a taster class:</p>\r\n\r\n<p>Participant name - {user_name}</p>\r\n\r\n<p>Parent Email - {parent_email}&nbsp;</p>\r\n\r\n<p>Parent Telephone - {parent_telephone}&nbsp;</p>\r\n\r\n<p>Subject&nbsp;- {subject}</p>\r\n\r\n<p>Message - {message}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>The taster session is not yet comfirmed until DRH Sports has been in contact to confirm</p>\r\n\r\n<p>Thanks!</p>', '2020-11-11 04:00:28', '2021-01-08 04:29:32'),
