(987, 0, 'tennis_pro_sec1_link', 'http://49.249.236.30:8654/dominic-new/user/badges', 'tennis-pro', NULL, '2020-05-19 06:24:29', '2020-12-28 08:50:44'),
(988, 0, 'tennis_pro_htw_title', 'How It Works', 'tennis-pro', NULL, '2020-05-19 06:24:29', '2020-10-04 08:52:43'),
(989, 0, 'tennis_pro_htw_desc', '<p style="text-align: center;">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><span style="font-size:16px;"><strong>DRH Tennis Pro</strong> allows you to connect with your tennis coach like never before by linking both coach and player accounts and allowing both sides to share information on the player&#39;s tennis development. There are 6 main elements of the DRH Tennis Pro feature. </span><br />\r\n&nbsp;</p>\r\n\r\n<p style="text-align: center;"><span style="font-size:18px;">They are: <strong>Player Goals</strong>, <strong>Player Badges</strong>, <strong>The&nbsp;Leaderboard</strong>,&nbsp;<strong>Player Reports</strong>, <strong>Matches&nbsp;</strong>and <strong>Stats</strong>.&nbsp;</span><br />\r\n&nbsp;</p>\r\n\r\n<p style="text-align: center;"><span style="font-size:16px;">Each element of the DRH Tennis Pro feature complements the tennis coaching experience between the player and the caoah. Each element is explained below.&nbsp;<br />\r\n<br />\r\nAt DRH Sports we&#39;re always looking to improve and provide the best experience possible for our players and coaches. If you have any feedback about any of the DRH Tennis Pro features, please do not hesitate to contact us at tennispro@drhsports.co.uk</span></p>', 'tennis-pro', NULL, '2020-05-19 06:24:29', '2020-12-30 22:51:45'),
(990, 0, 'ten_pro_lastsec_text', 'Enrol on a DRH Sports tennis coaching course at any of our venues and get all of the DRH Tennis Pro features included!', 'tennis-pro', NULL, '2020-05-19 06:24:29', '2020-10-04 10:25:02'),
